PROVIDER_HTTP='https://bsc-dataseed.binance.org'
web3 = Web3(Web3.HTTPProvider(PROVIDER_HTTP))

contractFactoryAddress = web3.toChecksumAddress('0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73')
contractFactory = web3.eth.contract(address=contractFactoryAddress, abi=abiFactory)
allPair = contractFactory.functions.allPairsLength().call()

with open('whitelist.json', 'rb') as wfp:
    try:
        whitelist = set(json.load(wfp))
    except:
        whitelist = set()

with open('lastpair', 'r') as wfp:
    fromPair = int(wfp.read())


for i in range(fromPair, allPair):
    pair = contractFactory.functions.allPairs(i).call()
    contractPairAddress = web3.toChecksumAddress(pair)
    contractPair = web3.eth.contract(address=contractPairAddress, abi=abiPair)
    token0 = contractPair.functions.token0().call()
    token1 = contractPair.functions.token1().call()
    
    _checkToken0 = checkToken(token0)
    _checkToken1 = checkToken(token1)

    if token0 not in whitelist:
        _checkToken0 = checkToken(token0)
        if _checkToken0:
            whitelist.add(token0)
    if token1 not in whitelist:
        _checkToken1 = checkToken(token1)
        if _checkToken1:
            whitelist.add(token1)

    if not(_checkToken0) and not(_checkToken1):
        continue
    
    with open('lastpair', 'w') as wfp:
        wfp.write(str(i))
    
    with open("whitelist.json", "w") as wfp:
        json.dump(list(whitelist), wfp)


